import React, { useState } from 'react';
import { ConversationList, Conversation, Avatar, Search } from '@chatscope/chat-ui-kit-react';
import '../styles/Sidebar.css';
import { loggedUser, conversations } from '../mockData';

const Sidebar = ({ selectedUser, handleUserClick }) => {
    const [searchTerm, setSearchTerm] = useState('');

    const filteredConversations = conversations.filter(conversation =>
        conversation.plate.toLowerCase().includes(searchTerm.toLowerCase())
    );

    return (
        <div className="sidebar">
            <div className="sidebar-header">
                <Avatar src="https://via.placeholder.com/150" name={loggedUser} />
                <span className="sidebar-username">{loggedUser}</span>
            </div>
            <div className="sidebar-search">
                <Search
                    placeholder="Search..."
                    value={searchTerm}
                    onChange={(value) => setSearchTerm(value)}
                    onClearClick={() => setSearchTerm('')}
                />
            </div>
            <div className="sidebar-spacer"></div>
            <ConversationList>
                {filteredConversations.map((conversation, index) => (
                    <Conversation
                        key={index}
                        name={conversation.plate}
                        lastSenderName={conversation.prefix}
                        info={conversation.info}
                        active={conversation.plate === selectedUser}
                        onClick={() => handleUserClick(conversation.plate)}
                    >
                        <Avatar src={conversation.avatar} name={conversation.plate} status={conversation.status} />
                        <div className="conversation-details">
                            <span className="conversation-name">{conversation.plate}</span>
                            <span className="conversation-prefix">{conversation.prefix}</span>
                            {conversation.unreadCount > 0 && (
                                <div className="unread-count">
                                    {conversation.unreadCount}
                                </div>
                            )}
                        </div>
                    </Conversation>
                ))}
            </ConversationList>
        </div>
    );
};

export default Sidebar;
