    return (
        <div style={{ width: '300px', borderRight: '1px solid #ccc' }}>
            <ConversationList>
                <Conversation
                    name="John Doe"
                    lastSenderName="John"
                    info="Hello, how are you?"
                />
                <Conversation
                    name="Jane Smith"
                    lastSenderName="Jane"
                    info="Are we still on for today?"
                />
                {/* Adicione mais conversas conforme necess√°rio */}
            </ConversationList>
        </div>
    );
};

export default Sidebar;
