import React, { useState } from 'react';
import { ConversationList, Conversation, Avatar, Search } from '@chatscope/chat-ui-kit-react';
import '../styles/Sidebar.css';

const Sidebar = () => {
    const [searchTerm, setSearchTerm] = useState('');

    return (
        <div className="sidebar">
            <div className="sidebar-search">
                <Search
                    placeholder="Search..."
                    value={searchTerm}
                    onChange={(value) => setSearchTerm(value)}
                    onClearClick={() => setSearchTerm('')}
                />
            </div>
            <ConversationList>
                <Conversation
                    name="John Doe"
                    lastSenderName="John"
                    info="Hello, how are you?"
                    avatar={<Avatar src="https://via.placeholder.com/40" name="John Doe" />}
                    className="conversation"
                />
                <Conversation
                    name="Jane Smith"
                    lastSenderName="Jane"
                    info="Are we still on for today?"
                    avatar={<Avatar src="https://via.placeholder.com/40" name="Jane Smith" />}
                    className="conversation"
                />
            </ConversationList>
        </div>
    );
};

export default Sidebar;
