import React, { useEffect, useState } from 'react';
import socket from './socket';
import './App.css'; // Importar o arquivo CSS

const ChatApp = () => {
    const [messages, setMessages] = useState([]);
    const [messageInput, setMessageInput] = useState('');

    useEffect(() => {
        socket.connect();

        socket.on('connect', () => {
            console.log('Connected to server');
        });

        socket.on('_chat_message', (data) => {
            setMessages((prevMessages) => [...prevMessages, { text: data.message, type: 'received' }]);
        });

        return () => {
            socket.off('connect');
            socket.off('_chat_message');
            socket.disconnect();
        };
    }, []);

    const sendMessage = () => {
        if (messageInput.trim()) {
            socket.emit('send_message', { message: messageInput });
            setMessages((prevMessages) => [...prevMessages, { text: messageInput, type: 'sent' }]); // Adiciona a mensagem localmente
            setMessageInput('');
        }
    };

    const handleKeyPress = (event) => {
        if (event.key === 'Enter') {
            sendMessage();
        }
    };

    return (
        <div>
            <h1>Chat App</h1>
            <div id="messages">
                {messages.map((message, index) => (
                    <div key={index} className={message.type === 'sent' ? 'message sent' : 'message received'}>
                        {message.text}
                    </div>
                ))}
            </div>
            <input
                id="messageInput"
                type="text"
                placeholder="Type a message"
                value={messageInput}
                onChange={(e) => setMessageInput(e.target.value)}
                onKeyPress={handleKeyPress}
            />
            <button onClick={sendMessage}>Send</button>
        </div>
    );
};

export default ChatApp;
